mapper.select:
  		select u.id ,u.score,u.stu_id,u.del_flag,u.create_by,u.create_date createDate,u.update_date updateDate,u.update_date,u.sub_id,u.remarks,us.username username,su.name subname,de.dept_id deptId,crea.username creator,upd.username updator,su.grade_id gradeId from t_score u
  		left join t_user us on us.user_id=u.stu_id
	  	left join t_dept de on de.dept_id=us.dept_id
	  	left join t_subject su on su.id=u.sub_id
	  	left join t_user crea on crea.user_id=u.create_by
	  	left join t_user upd on upd.user_id=u.update_by
  		where 1=1

        	AND u.score = #{score}


		  	AND u.stu_id = #{stuId}


	        AND d.class_id = #{classId}


			AND d.sub_id = #{subId}


		  	AND (de.dept_id = #{deptId} OR de.parent_id = #{deptId})


		  	AND su.grade_id = #{gradeId}


			AND us.username  like concat(concat('%',#{username,jdbcType=VARCHAR}),'%')


			AND su.name  like concat(concat('%',#{subname,jdbcType=VARCHAR}),'%')

  		order by u.id
